import React, { useState, useEffect } from "react";
import logo from "./logo.svg";
import "./App.css";
import Svgs from "./Svgs";

const App = () => {
  const [redColor, setRedColor] = useState(0);
  const [greenColor, setGreenColor] = useState(0);
  const [blueColor, setBlueColor] = useState(0);
  const [bgcolor, setBgColor] = useState("pink");

  const setColor = e => {
    if (e.target.id === "red_slider") {
      setRedColor(Number(e.target.value));
      setBgColor("red");
    } else if (e.target.id === "green_slider") {
      setGreenColor(Number(e.target.value));
      setBgColor("green");
    } else if (e.target.id === "blue_slider") {
      setBlueColor(Number(e.target.value));
      setBgColor("blue");
    }
  };

  console.log(redColor, greenColor, blueColor);

  return (
    <div className="container">
      <div className="svgs">{Svgs.warriors}</div>
      <div id="color_box">
        <input
          type="range"
          min="0"
          max="255"
          class="slider"
          id="red_slider"
          onChange={e => setColor(e)}
        />
        <p>
          Red: <span id="red_value">{redColor}</span>
        </p>

        <input
          type="range"
          min="0"
          max="255"
          class="slider"
          id="green_slider"
          onChange={e => setColor(e)}
        />
        <p>
          Green: <span id="green_value">{greenColor}</span>
        </p>

        <input
          type="range"
          min="0"
          max="255"
          class="slider"
          id="blue_slider"
          onChange={e => setColor(e)}
        />
        <p>
          Blue: <span id="blue_value">{blueColor}</span>
        </p>
      </div>
      <div
        className="colorhex"
        style={{
          width: "200px",
          height: "200px",
          background: "rgb(${redColor}, 230, 210)"
        }}
      />
    </div>
  );
};

export default App;
