const reducer = (state, action) => {
  console.log("state in reducer: ", state);
  switch (action.payload) {
    case "UPDATE_TEAM":
      console.log("action payload: ", action.payload);
      return action.payload;
    default:
      return state;
  }
};

export default reducer;
