import React, { useEffect } from "react";
import Svg from "./Svgs";

const SvgComponent = ({ title, handleLogoClick }) => {
  useEffect(() => {
    const paths = document.querySelectorAll("path");
    paths.addEventListener("click", handlePathClick);
  });

  handlePathClick = e => {
    console.log("path id: ", event.target.id);
  };
  return (
    <div className="svg test" onClick={handleLogoClick}>
      {Svg[title]}
    </div>
  );
};

export default SvgComponent;
