import React, { useState, useEffect } from "react";
import logo from "./logo.svg";
import "./App.css";
import Svgs from "./Svgs";

const App = () => {
  const [redColor, setRedColor] = useState(0);

  useEffect(() => {
    const inputs = document.querySelectorAll("input");
    inputs.forEach(input =>
      input.addEventListener("input", debounce(setColor))
    );
  });

  const setColor = e => {
    switch (e.target.id) {
      case "red_slider":
        setRedColor(e.target.value);
    }
  };

  const debounce = (fn, time) => {
    let timeout;

    return function(...args) {
      const functionCall = () => fn.apply(this, args);

      clearTimeout(timeout);
      timeout = setTimeout(functionCall, time);
    };
  };

  return (
    <div className="container">
      <div className="svgs">{Svgs.warriors}</div>
      <div id="color_box">
        <input type="range" min="0" max="255" class="slider" id="red_slider" />
        <p>
          Red: <span id="red_value">{redColor}</span>
        </p>

        <input
          type="range"
          min="0"
          max="255"
          value="127"
          class="slider"
          id="green_slider"
        />
        <p>
          Green: <span id="green_value">127</span>
        </p>

        <input
          type="range"
          min="0"
          max="255"
          value="127"
          class="slider"
          id="blue_slider"
        />
        <p>
          Blue: <span id="blue_value">127</span>
        </p>
      </div>
      <div className="colorhex" />
    </div>
  );
};

export default App;
